mapper.select:
		select id,interface_name,interface_efficiency,call_province,call_date,operation_time,Type from all_interface where 1=1 

			and interface_name like concat('%',#{obj.interface_name},'%')


			and call_province like concat('%',#{obj.call_province},'%')


            and operation_time >= #{obj.minCalltime}


            and operation_time <= #{obj.maxCalltime}

		limit #{strat},#{pagecount}

		select count(1) from all_interface where 1=1

			and interface_name like concat('%',#{obj.interface_name},'%')


			and call_province like concat('%',#{obj.call_province},'%')


            and operation_time >= #{obj.minCalltime}


            and operation_time <= #{obj.maxCalltime}
